		add_action('wp_ajax_nopriv_wct_block_cond_logic_field_type', array($this, 'wct_block_cond_logic_field_type'));

		add_action( 'widgets_init', array($this, 'wct_register_sidebar') );
		add_action( 'admin_footer', array($this, 'wct_style_admin_footer') );

		add_action( 'woocommerce_admin_order_data_after_billing_address', array($this, 'wct_add_custom_field_to_order_detail_admin'), 10, 1 );

		//
		add_action('wp_ajax_nopriv_wct_export', array($this, 'wct_export_data_subscribers'));
        add_action('wp_ajax_wct_export', array($this, 'wct_export_data_subscribers'));

