	/**
	* On/Off product tour
	* @since 1.0
	*/
	public function wct_save_product_tour(){

		$status = array('status'=>'0');

		if( isset( $_POST['tour_done'] ) && !empty( $_POST['tour_done'] ) ){

			if( $_POST['step'] == '1' ){

				update_option( 'wct_product_tour_1', '2' );

			}else{

				update_option( 'wct_product_tour_2', '2' );
			}

			$status['status'] = '1';
		}

		echo json_encode($status);
		die();

	}

	/**
	* Plugin active.
	* Set default: template active
	* Templat active: Step - modern(2).
	* @since    1.0
	*/
	public static function wct_active() {

		if(!get_option('wct_product_tour_1')){
			add_option('wct_product_tour_1', '1');
		}
		if(!get_option('wct_product_tour_2')){
			add_option('wct_product_tour_2', '1');
		}
		if(!get_option('template_active')){
			add_option('template_active', '2');
		}
		if(!get_option('wct_wpml')){
			add_option('wct_wpml', 'off');
		}
		if(!get_option('wct_admin_html_row')){
			add_option('wct_admin_html_row', self::$wct_admin_html_row_2);
		}
		if(!get_option('template_shortcode')){
			add_option('template_shortcode', self::$template_shortcode_2);
		}

	}

