    	add_submenu_page('wct-settings', __('Support', 'wct'), __('Support', 'wct'), 'manage_options', 'wct-support', array($this, 'wct_support_page'));
    	add_submenu_page('wct-settings', __('Document', 'wct'), __('Document', 'wct'), 'manage_options', 'wct-document', array($this, 'wct_document_page'));
    }

