	/**
	* Get attribute taxonomies.
	* @return array
	* @since    1.0
	*/
	public static function wct_get_attribute(){
		include_once( ABSPATH . 'wp-content/plugins/woocommerce/woocommerce.php' );
		$attribute_taxonomies = wc_get_attribute_taxonomies();
		$taxonomy_terms = array();

		if ( $attribute_taxonomies ) :
		    foreach ($attribute_taxonomies as $tax) :
			   	if (taxonomy_exists(wc_attribute_taxonomy_name($tax->attribute_name))) :
			        $taxonomy_terms[$tax->attribute_name] = get_terms( wc_attribute_taxonomy_name($tax->attribute_name), 'orderby=name&hide_empty=0' );
			    endif;
			endforeach;
		endif;

		return $taxonomy_terms;
	}

	/**
	* Get language.
	* Plugin: WPML
	* @return array
	* @since    1.0
	*/
	public static function wct_get_languages(){
		$languages_arr = array();
		$wct_wpml = get_option('wct_wpml');
		include_once( ABSPATH . 'wp-admin/includes/plugin.php' );
		if (is_plugin_active( 'sitepress-multilingual-cms/sitepress.php' ) && $wct_wpml == 'on') {
			$languages = icl_get_languages('skip_missing=0&orderby=code');
			if($languages){
				foreach($languages as $l){
					$languages_arr[] = array('active'=> $l['active'], 'default_locale' => $l['language_code'], 'translated_name' => $l['translated_name'], 'country_flag_url' => $l['country_flag_url']);
				}    
			}
		}

		return $languages_arr;
	}

	/**
	* Show field title with multilanguage.
	* $type: title or placeholder
	* @since    1.0
	*/
	public static function wct_show_title_field($id, $type){

		if($type == 'title'){
			$out = 'title';
		}else{
			$out = 'title_h';
		}

		$languages = self::wct_get_languages();

		/**
		* Edit tab language display
		* @since 1.0.1
		*/

		if(!empty($languages)){

			echo '<div class="tabs animated-fade ossvn-tabs-languages">';

				echo '<ul class="tab-links">';
					$i = 0;$ii = 0;
					foreach($languages as $key_lag){

						if($key_lag['active'] == 1){$active = 'active';}else{$active = '';}
						echo '<li class="'.esc_attr($active).'"><a href="#tab-'.$i.'"><img src="'.esc_attr($key_lag['country_flag_url']).'"></a></li>';
					$i++;}
				echo '</ul>';

				echo '<div class="tab-content">';
					foreach($languages as $key_lag){
						if($key_lag['active'] == 1){$active = 'active';}else{$active = '';}
						echo '<div id="tab-'.$ii.'" class="tab '.esc_attr($active).'">';
							echo '<input type="text" id="ossvn-'.$out.'-id-'.esc_attr($key_lag['default_locale']).'" data-target="block_'.$out.'_'.esc_attr($key_lag['default_locale']).'" class="ossvn-get-data" />';
						echo '</div>';
					$ii++;}
				echo '</div>';
				echo '<input type="hidden" id="ossvn-checkbox-id" data-target="block_'.$out.'" class="ossvn-get-data" />';
			echo '</div>';

		}else{
			echo '<input type="text" id="ossvn-checkbox-id" data-target="block_'.$out.'" class="ossvn-get-data" />';
		}

		/**
		* Allow tag <a> in label
		*
		* @since 1.2
		*/
		if($type == 'title'){
			echo '
			<label class="ossvn-label" for="block_title_url" style="margin-top: 20px;">'.__('Add url to title', 'wct').'</label>
			<input type="text" data-target="block_title_url" class="ossvn-get-data" />
			';
		}

	}


	/**
	* Set template active with template html, shortcode
	* $template: number - template is active
	* $status: string - active, save_template, restore
	* $args: array()
	* @since 1.0
	*/
	private function wct_set_template_active( $template, $status = 'active', $args = array() ){

		$defaults = array( 'html'=> '', 'shortcode'=>'' );
		$args = wp_parse_args( $args, $defaults );

		update_option( 'wct_admin_html_row', $args['html'] );
		update_option( 'template_shortcode', $args['shortcode'] );

		if($status == 'save_template'){

			if($template == '_blank'){

				update_option( 'wct_admin_html_row'.$template.'', stripslashes($args['html']) );
				update_option( 'template_shortcode'.$template.'', $args['shortcode'] );
			}else{

				update_option( 'wct_admin_html_row_'.$template.'', stripslashes($args['html']) );
				update_option( 'template_shortcode_'.$template.'', $args['shortcode'] );
			}

		}elseif($status == 'restore'){

			switch ($template) {
				case '1':
					update_option( 'wct_admin_html_row_1', stripslashes( self::$wct_admin_html_row_1 ) );
					update_option( 'template_shortcode_1', self::$template_shortcode_1 );
					break;
				case '2':
					update_option( 'wct_admin_html_row_2', stripslashes( self::$wct_admin_html_row_2 ) );
					update_option( 'template_shortcode_2', self::$template_shortcode_2 );
					break;
				case '3':
					update_option( 'wct_admin_html_row_3', stripslashes( self::$wct_admin_html_row_3 ) );
					update_option( 'template_shortcode_3', self::$template_shortcode_3 );
					break;
				case '4':
					update_option( 'wct_admin_html_row_4', stripslashes( self::$wct_admin_html_row_4 ) );
					update_option( 'template_shortcode_4', self::$template_shortcode_4 );
					break;
				case '5':
					update_option( 'wct_admin_html_row_5', stripslashes( self::$wct_admin_html_row_5 ) );
					update_option( 'template_shortcode_5', self::$template_shortcode_5 );
					break;
				default:
					update_option( 'wct_admin_html_row_blank', stripslashes( self::$wct_admin_html_row ) );
					update_option( 'template_shortcode_blank', self::$template_shortcode );
					break;
			}

			update_option( 'wct_styling', self::$wct_stylings );

		}else{

			switch ($template) {
				case '1':
					if(!get_option('wct_admin_html_row_1')){update_option( 'wct_admin_html_row_1', stripslashes( self::$wct_admin_html_row_1 ) );}
					if(!get_option('template_shortcode_1')){update_option( 'template_shortcode_1', self::$template_shortcode_1 );}
					break;
				case '2':
					if(!get_option('wct_admin_html_row_2')){update_option( 'wct_admin_html_row_2', stripslashes( self::$wct_admin_html_row_2 ) );}
					if(!get_option('template_shortcode_2')){update_option( 'template_shortcode_2', self::$template_shortcode_2 );}
					break;
				case '3':
					if(!get_option('wct_admin_html_row_3')){update_option( 'wct_admin_html_row_3', stripslashes( self::$wct_admin_html_row_3 ) );}
					if(!get_option('template_shortcode_3')){update_option( 'template_shortcode_3', self::$template_shortcode_3 );}
					break;
				case '4':
					if(!get_option('wct_admin_html_row_4')){update_option( 'wct_admin_html_row_4', stripslashes( self::$wct_admin_html_row_4 ) );}
					if(!get_option('template_shortcode_4')){update_option( 'template_shortcode_4', self::$template_shortcode_4 );}
					break;
				case '5':
					if(!get_option('wct_admin_html_row_5')){update_option( 'wct_admin_html_row_5', stripslashes( self::$wct_admin_html_row_5 ) );}
					if(!get_option('template_shortcode_5')){update_option( 'template_shortcode_5', self::$template_shortcode_5 );}
					break;
				default:
					if(!get_option('wct_admin_html_row_blank')){update_option( 'wct_admin_html_row_blank', stripslashes( self::$wct_admin_html_row ) );}
					if(!get_option('template_shortcode_blank')){update_option( 'template_shortcode_blank', self::$template_shortcode );}
					break;
			}
		}

	}


	/**
	* Ajax save template step 1.
	* @since    1.0
	*/
	public function wct_save_template_step_1(){
		
		$status = array('status'=> '0');
		
		if(isset($_POST['template_active']) && !empty($_POST['template_active'])){
			
			update_option('template_active', $_POST['template_active']);

			$this->wct_set_template_active( $_POST['template_active'], 'active', array() );


		}else{
			update_option('template_active', '2');
			$this->wct_set_template_active( '2', 'active', array() );
		}

		if(isset($_POST['wpml']) && !empty($_POST['wpml'])){
			update_option('wct_wpml', $_POST['wpml']);
		}else{
			update_option('wct_wpml', 'off');
		}

		if(isset($_POST['i_sell']) && !empty($_POST['i_sell'])){
			update_option('wct_i_sell', $_POST['i_sell']);
		}else{
			update_option('wct_i_sell', 'off');
		}

		if(isset($_POST['newlleter']) && !empty($_POST['newlleter'])){
			update_option('wct_newlleter', $_POST['newlleter']);
		}else{
			update_option('wct_newlleter', 'off');
		}


		update_option('_wct_logic_fields', '');

		if( !get_option('wct_styling') ){

			update_option( 'wct_styling', self::$wct_stylings );
		}

		echo json_encode($status);
		die();
	}

