	/**
	* Update block rule conditional logic
	* @since 1.0
	*/
	public function wct_update_block_rule_conditional_logic(){
		
		$status = array('status'=>'0');
		$arr = array();

		if( isset($_POST['status']) && $_POST['status'] == 'on' ){

			if(isset($_POST['selector_id']) && !empty($_POST['selector_id'])){
			
				if(isset($_POST['logic_field']) && !empty($_POST['logic_field'])){

					if($wct_logic_fields = get_option('_wct_logic_fields')){

						$wct_logic_fields[$_POST['selector_id']]['status'] = 'on';
						$wct_logic_fields[$_POST['selector_id']]['show_hide'] = $_POST['show_type'];
						$wct_logic_fields[$_POST['selector_id']]['all_any'] = $_POST['all_type'];
						$wct_logic_fields[$_POST['selector_id']]['fields'] = $_POST['logic_field'];
						update_option('_wct_logic_fields', $wct_logic_fields);
						$status['status'] = '1';
						
					}else{

						$arr[$_POST['selector_id']] = array('status'=> 'on', 'show_hide' => $_POST['show_type'], 'all_any' => $_POST['all_type'], 'fields'=> $_POST['logic_field']);
						update_option('_wct_logic_fields', $arr);
						$status['status'] = '2';
						
					}
				}
			}

		}else{

			if($wct_logic_fields = get_option('_wct_logic_fields')){
						
				$wct_logic_fields[$_POST['selector_id']]['status'] = 'off';
				$wct_logic_fields[$_POST['selector_id']]['show_hide'] = 'show';
				$wct_logic_fields[$_POST['selector_id']]['all_any'] = 'all';
				update_option('_wct_logic_fields', $wct_logic_fields);
				$status['status'] = '3';
				
			}
		}

		echo json_encode($status);
		exit();
	}

	/**
	* Show block rule conditional logic
	* @since
	*/
	public function wct_show_block_rule_conditional_logic(){
		
		$status = array('status'=> '0', 'html' => '');
		
		$out_group_logic_field = '';

		$html = '';

		if(isset($_POST['selector_id']) && !empty($_POST['selector_id'])){

			if(isset($_POST['all_fields']) && !empty($_POST['all_fields'])){

				$all_field = $_POST['all_fields'];
				
				$html .= '
						<div class="add-block-cond-logic">
							'.$this->wct_out_select_logic_field( $_POST['selector_id'], 'wct', $all_field ).'
							'.$this->wct_out_select_logic_field_type().'
							<input type="text" id="block-cond-logic-field-is-value"  data-target="block_cond_logic_field_is_value" class="ossvn-get-data"/>
							<a href="" class="remove-block-cond-logic"><i class="fa fa-times"></i></a>
						</div>
				';
				$status['html'] = $html;

				/*======================*/
				if( $option_all_fields = get_option('_wct_logic_fields') ){
					
					if( isset( $option_all_fields[ $_POST['selector_id'] ]['fields'] ) && !empty( $option_all_fields[ $_POST['selector_id'] ] ) ){
						
						foreach ( $option_all_fields[$_POST['selector_id']]['fields'] as $key => $value ) {

							$out_group_logic_field .= '
												<div class="add-block-cond-logic" style="margin-bottom: 10px;">
													'.$this->wct_out_select_logic_field( $_POST['selector_id'], $value['logic_field'], $all_field ).'
													'.$this->wct_out_select_logic_field_type($value['logic_field_is']).'
													<input type="text" id="block-cond-logic-field-is-value"  data-target="block_cond_logic_field_is_value" class="ossvn-get-data" value="'.esc_attr( $value['logic_field_is_value'] ).'" />
													<a href="" class="remove-block-cond-logic"><i class="fa fa-times"></i></a>
												</div>
							';
						}

						$status['html'] = $out_group_logic_field;
					}

				}
				/*=====================*/

			}
		}

		echo json_encode($status);
		exit();
	}

	/**
	* Show select logic field type
	* Param 1: $type - type field
	* @return html - <select></select>
	*/
	private function wct_out_select_logic_field_type( $type = null ){

		$args = array(
			'is' => __('is', 'wct'),
			'isnot' => __('is not', 'wct'),
			'contains' => __('contains', 'wct'),
			'checked' => __('checked', 'wct'),
			'selected' => __('selected', 'wct'),
			);
		$out = '<select id="block_cond_logic_field_type" data-target="block_cond_logic_field_is" class="ossvn-get-data">';
			foreach ($args as $key => $value) {
				
				if( isset($type) && !empty($type) ){
					
					if( $type == $key ){

						$selected = 'selected="selected"';

					}else{

						$selected = '';
					}

				}else{
					$selected = '';
				}

				$out .= '<option value="'.esc_attr($key).'" '.esc_attr($selected).'>'.esc_html($value).'</option>';
			}
		$out .= '</select>';

		return $out;
	}


	/**
	* Show select logic field
	* Param 1: $selector_id - dependsOn() field
	* Param 2: $args - array()
	* @return html - <select></select>
	*/
	private function wct_out_select_logic_field( $selector_id, $logic_field, $args = array() ){

		$out = '<select id="block_cond_logic_field" data-target="block_cond_logic_field" class="block-cond-logic-field ossvn-get-data">';
			
			foreach ($args as $value) {

				if( isset($logic_field) && $logic_field == $value['block_id'] ){
					
					$selected = true;
				}else{

					$selected = false;
				}

				$out .= $this->wct_out_option_logic_field( $value['block_type'], $value['block_id'], $value['block_title'], $selected, $selector_id );
			}

		$out .= '</select>';

		return $out;
	}

	/**
	* Show option select logic field
	* Param 1: $type - block type
	* Param 2: $block_id - block id
	* Param 3: $block_title - block title
	* Param 4: $selected - true/false
	* Param 5: $selector_id - dependsOn() field
	* @return: html - <option></option>
	* @since 1.0
	*/
	private function wct_out_option_logic_field( $type, $block_id, $block_title, $selected, $selector_id ){

		$out = '';

		switch ($type) {
			case 'title':
				break;
			case 'login_form':
				break;
			case 'new_account':
				break;
			case 'sidebar':
				break;
			case 'your_order':
				break;
			case 'payment':
				break;
			case 'process':
				break;
			case 'step':
				break;
			case 'order_success':
				break;
			case 'different_address':
				break;
			default:
				if( $selector_id != $block_id ){

					if( $selected == true ){
							
						$selected_attr = 'selected="selected"';
					}else{

						$selected_attr = '';
					}

					$out .= '<option value="'.esc_attr($block_id).'" '.esc_attr($selected_attr).'>'.esc_html($block_title).' ('.esc_attr($block_id).')</option>';
				}

				break;
		}

		return $out;

	}



	/**
	* Get all field type: checkbox, radio
	* @return json
	* @since 1.0
	*/
	public function wct_block_cond_logic_field_type(){
		$status = array('status'=> '0', 'html' => '');
		$out = '';
		if(isset($_POST['all_fields']) && !empty($_POST['all_fields'])){
			$all_field = $_POST['all_fields'];
			foreach ($all_field as $key => $value) {
				if($value['block_type'] == 'checkbox' || $value['block_type'] == 'radio'){
					$out .= '<option value="'.esc_attr($value['block_id']).'">'.esc_html($value['block_title']).' ('.esc_attr($value['block_id']).')</option>';
				}
			}
			$status['html'] = $out;
		}
		echo json_encode($status);
		exit();
	}

	/**
	* Export data subscribers
	* @since 1.0
	*/
	public function wct_export_data_subscribers(){
		
		$status = array('status'=>false);

		if( $database = get_option('wct_newletter_database') ){
			
			if(!empty($database)){

				$realContent = join( "\n", $database);
				$content = serialize($database);
		        header("HTTP/1.1 200 OK");
		        $file_name = "subscribers-email.csv";
		        header('Content-Type: text/csv');
		        $fsize = strlen($content);
		        header("Cache-Control: must-revalidate, post-check=0, pre-check=0");
		        header('Content-Description: File Transfer');
		        header("Content-Disposition: attachment; filename=" . $file_name);
		        header("Content-Length: ".$fsize);
		        header("Expires: 0");
		        header("Pragma: public");
		        echo $realContent;
			}
		}
		die();
	}

