	/**
	* AJAX save product logic
	*
	* @author Comfythemes
	* @since 1.3
	*/
	public function wct_save_logic_product(){
		
		$status = array('status'=>'0');
		$wct_logic_fields = get_option('_wct_logic_product_fields') ? get_option('_wct_logic_product_fields') : array();

		if( isset($_POST['selector_id']) && !empty($_POST['selector_id']) ){

			$wct_logic_fields[$_POST['selector_id']]['status'] 		= ( isset($_POST['status']) && $_POST['status'] == 'on' ) ? 'on' : 'off';
			$wct_logic_fields[$_POST['selector_id']]['products']	= ( isset($_POST['products']) && !empty($_POST['products']) ) ? $_POST['products'] : array();

		}

		update_option('_wct_logic_product_fields', $wct_logic_fields);

		echo json_encode($status);
		exit();

	}

	/**
	* AJAX load product logic
	*
	* @author Comfythemes
	* @since 1.3
	*/
	public function wct_load_logic_product(){
		
		$status = array('status'=>'0', 'html' => '');
		$wct_logic_fields = get_option('_wct_logic_product_fields') ? get_option('_wct_logic_product_fields') : array();
		$out = '';

		if( isset($_POST['selector_id']) && !empty($_POST['selector_id']) && isset($wct_logic_fields[$_POST['selector_id']]['products']) && !empty($wct_logic_fields[$_POST['selector_id']]['products']) ):
			
			$args = array( 'post_type' => 'product', 'posts_per_page' => -1 );
			$products = new WP_Query($args);

				foreach ( $wct_logic_fields[$_POST['selector_id']]['products'] as $value ) {

					if( $products->have_posts() ){

						$out .= '<div class="add-block-cond-logic-product"><select name="block_logic_product" data-target="block_logic_product" class="ossvn-get-data">';
							$out .= '<option value="">'.esc_html__( 'Select product...', 'wct' ).'</option>';
							while( $products->have_posts() ){ $products->the_post();

								$selected = ( get_the_ID() == $value ) ? 'selected="selected"' : '';

								$out .= '<option value="'.esc_attr(get_the_ID()).'" '.$selected.'>'.esc_html( get_the_title() ).'</option>';

							}
						$out .= '</select><a href="#" class="remove-block-cond-logic-product"><i class="fa fa-times"></i></a></div>';
					}

				}

			wp_reset_postdata();

			$status['status'] = 1;
			$status['html'] = $out;

		endif;

		echo json_encode($status);
		exit();

	}


